import React, { useState } from "react";

const SearchParams = () => {
  const [location, setLocation] = useState("Montreal");

  return (
    <div className="search-params">
      <form>
        <label htmlFor="location">
          location
          <input
            id="location"
            value={location}
            placeholder={location}
            onChange={(e) => setLocation(e.target.value)}
          />
          <button>Submit</button>
        </label>
      </form>
    </div>
  );
};

export default function Filters({ data, dataClean, PriceData, setMarkers }) {
  const [checked, setChecked] = useState(false);
  const [selected, setSelected] = useState("");

  dataClean = [];

  if (checked) {
    data.forEach((element) => {
      if (element.Bedrooms === 2) {
        dataClean.push({
          position: { lng: element.Longitude, lat: element.Latitude },
          text: PriceData(element),
        });
      }
    });
  } else {
    console.log(`not checked ${dataClean.length}`);
    data.forEach((element) => {
      dataClean.push({
        position: { lng: element.Longitude, lat: element.Latitude },
        text: PriceData(element),
      });
    });
  }

  // Submit Function

  console.log(`global ${dataClean.length}`);

  const handleSubmit = (event) => {
    event.preventDefault();
    setMarkers(dataClean);
    console.log(`2 handleSubmit ${dataClean.length}`);
  };

  // Check the status of properties

  function handleChange(e) {
    setSelected(e.target.value);
  }

  return (
    <>
      <SearchParams />
      <form onSubmit={handleSubmit}>
        <label>
          2 bedrooms:
          <input
            type="checkbox"
            value="{checked}"
            onChange={() => setChecked((checked) => !checked)}
          />
        </label>

        <label>
          <select>
            <option value="allpropt">All Properties</option>
            <option value="avlpropt">Available Properties</option>
            <option value="removedpropt">Removed Properties</option>
          </select>
        </label>

        <input className="button" type="submit" value="Submit" />
      </form>
    </>
  );
}
