import React, { useState } from "react";
import { Map, TileLayer } from "react-leaflet";
import MarkerCluster from "./MarkerCluster";
import data from "./data";

const position = [45.503, -73.595];
const mapStyle = { height: "90vh" };

let dataClean = [];

data.forEach((element) => {
  dataClean.push({
    position: { lng: element.Longitude, lat: element.Latitude },
    text: PriceData(element),
  });
});

// Price Data Log function - new

function PriceData(element) {
  let labelText = `No.: ${element.MlsNumber}, ${element.Bedrooms} rooms</br>
  ${element.ListingAddress}</br></br>`;
  const data = element.history;
  data[0].forEach((element, indx) => {
    labelText = `${labelText} ∙ ${element}: ${data[1][indx]} </br>`;
  });

  labelText = `${labelText}</br>${element.days_on_market} days on market</br>status: ${element.status}`;

  return labelText;
}

const MainMap = () => {
  // approach 1: pre-filter dataClean here, then re-render map

  const [checked, setChecked] = useState(false);

  dataClean = [];

  const handleSubmit = (event) => {
    event.preventDefault();
    setMarkers(dataClean);
  };

  if (checked) {
    data.forEach((element) => {
      if (element.Bedrooms === 2) {
        dataClean.push({
          position: { lng: element.Longitude, lat: element.Latitude },
          text: PriceData(element),
        });
      }
    });
  } else {
    data.forEach((element) => {
      dataClean.push({
        position: { lng: element.Longitude, lat: element.Latitude },
        text: PriceData(element),
      });
    });
  }

  const [markers, setMarkers] = useState(dataClean);

  return (
    <>
      <form onSubmit={handleSubmit}>
        <label>
          2 bedrooms:
          <input
            type="checkbox"
            value="{checked}"
            onChange={() => setChecked((checked) => !checked)}
          />
        </label>
        <label>
          <label>
            <p>Apples</p>
            <select name="apple" onChange={handleChange}>
              <option value="">--Please choose an option--</option>
              <option value="fuji">Fuji</option>
              <option value="jonathan">Jonathan</option>
              <option value="honey-crisp">Honey Crisp</option>
            </select>
          </label>
          <label>
            <p>Count</p>
            <input
              type="number"
              name="count"
              onChange={handleChange}
              step="1"
            />
          </label>
          <label>
            <p>Gift Wrap</p>
            <input type="checkbox" name="gift-wrap" onChange={handleChange} />
          </label>
          Status:
          <input
            type="checkbox"
            value="{checked}"
            onChange={() => setChecked((checked) => !checked)}
          />
        </label>

        <input type="submit" value="Submit" />
      </form>

      <Map center={position} zoom={2} style={mapStyle} maxZoom={18}>
        <TileLayer
          url="http://{s}.tile.osm.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
        />
        <MarkerCluster markers={markers} />
      </Map>
    </>
  );
};

export default MainMap;
