import React, { useState } from "react";

export default function Filters({ data, dataClean, PriceData, setMarkers }) {
  const [checked, setChecked] = useState(false);
  const [all, setSelected] = useState(false);
  const [name, setName] = useState("");

  function handleChange(e) {
    setName(e.target.value);
  }

  dataClean = [];

  function pushData() {
    data.forEach((element) => {
      dataClean.push({
        position: { lng: element.Longitude, lat: element.Latitude },
        text: PriceData(element),
      });
    });
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    setMarkers(dataClean);
    addTask(name);
    setName("");
  };

  function addTask(name) {
    alert(name);
  }

  if (checked) {
    data.forEach((element) => {
      if (element.Bedrooms === 2) {
        dataClean.push({
          position: { lng: element.Longitude, lat: element.Latitude },
          text: PriceData(element),
        });
      }
    });
  } else {
    pushData();
  }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <label>
          2 bedrooms:
          <input
            type="checkbox"
            value="{checked}"
            onChange={() => setChecked((checked) => !checked)}
          />
        </label>
        <label>
          <select value="{all}" onChange={() => (addTask = { addTask })}>
            <option value="all">All Properties</option>
            <option value="available" onClick={() => alert("hi!")}>
              Available Properties
            </option>
            <option value="removed" onClick={() => alert("bye!")}>
              Removed Properties
            </option>
          </select>
        </label>
        <input
          type="text"
          id="new-todo-input"
          className="input input__lg"
          name="text"
          autoComplete="off"
          value={name}
          onChange={handleChange}
        />
        <input className="button" type="submit" value="Submit" />
      </form>
    </>
  );
}
