import React, { useState } from "react";

export default function Filters({ data, dataClean, PriceData, setMarkers }) {
  const [checked, setChecked] = useState(false);
  const [all, setSelected] = useState("");

  dataClean = [];

  function pushData() {
    data.forEach((element) => {
      dataClean.push({
        position: { lng: element.Longitude, lat: element.Latitude },
        text: PriceData(element),
      });
    });
  }

  // Submit Function

  const handleSubmit = (event) => {
    event.preventDefault();
    setMarkers(dataClean);
    setSelected("");
  };

  // Check the status of properties

  function handleChange(e) {
    setSelected(e.target.value);
    showStatus();
  }

  function showStatus(e) {
    if (e === "removed") {
      let statusResult = data.filter((element) => element.status === "Removed");
      dataClean = dataClean.concat(statusResult);
    } else if (e === "available") {
      let statusResultTwo = data.filter(
        (element) => element.status === "Available"
      );
      dataClean = dataClean.concat(statusResultTwo);
    }
  }

  // Check the bedrooms

  if (checked) {
    data.forEach((element) => {
      if (element.Bedrooms === 2) {
        dataClean.push({
          position: { lng: element.Longitude, lat: element.Latitude },
          text: PriceData(element),
        });
      }
    });
  } else {
    pushData();
  }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <label>
          2 bedrooms:
          <input
            type="checkbox"
            value="{checked}"
            onChange={() => setChecked((checked) => !checked)}
          />
        </label>
        <label>
          <select value={all} onChange={handleChange}>
            <option id="all" value="all">
              All Properties
            </option>
            <option id="avail" value="available">
              Available Properties
            </option>
            <option id="remov" value="removed">
              Removed Properties
            </option>
          </select>
        </label>

        <input className="button" type="submit" value="Submit" />
      </form>
    </>
  );
}
