import React, { useState } from "react";

export default function Filters({ data, dataClean, PriceData, setMarkers }) {
  const [checked, setChecked] = useState(false);
  const [all, setSelected] = useState("");

  function handleChange(e) {
    setSelected(e.target.value);
  }

  dataClean = [];

  function pushData() {
    data.forEach((element) => {
      dataClean.push({
        position: { lng: element.Longitude, lat: element.Latitude },
        text: PriceData(element),
      });
    });
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    setMarkers(dataClean);
    addTask(all);
    setSelected("");
  };

  function addTask(e) {
    alert(e);
  }

  if (checked) {
    data.forEach((element) => {
      if (element.Bedrooms === 2) {
        dataClean.push({
          position: { lng: element.Longitude, lat: element.Latitude },
          text: PriceData(element),
        });
      }
    });
  } else {
    pushData();
  }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <label>
          2 bedrooms:
          <input
            type="checkbox"
            value="{checked}"
            onChange={() => setChecked((checked) => !checked)}
          />
        </label>
        <label>
          <select value={all} onChange={handleChange}>
            <option value="all">All Properties</option>
            <option value="available">Available Properties</option>
            <option value="removed">Removed Properties</option>
          </select>
        </label>

        <input className="button" type="submit" value="Submit" />
      </form>
    </>
  );
}
